	public static void main(String[] args) {
		// TODO Auto-generated method stub

for (float i = 0; i <= 10; i+=2) {
	System.out.println("Cambalhota sequêncial loopFor 111111111111 : "+i);
	

for (float j = 0; j <= 10; j++) {
	System.out.println("Cambalhota sequêncial loopFor 2222 : "+j);
	
}	
	
}		
	}

